package peladas;

import java.util.Date;

public class Peladas {

	private String nomeEventEsportivo;

	private Date dataEventEsportivo;

	private int horaDoEventoEsportivo;

	private String localDoEvento;

	public Peladas() {
		// TODO Auto-generated constructor stub
	}

	public String getNomeEventEsportivo() {
		return nomeEventEsportivo;
	}

	public void setNomeEventEsportivo(String nomeEventEsportivo) {
		this.nomeEventEsportivo = nomeEventEsportivo;
	}

	public Date getDataEventEsportivo() {
		return dataEventEsportivo;
	}

	public void setDataEventEsportivo(Date dataEventEsportivo) {
		this.dataEventEsportivo = dataEventEsportivo;
	}

	public int getHoraDoEventoEsportivo() {
		return horaDoEventoEsportivo;
	}

	public void setHoraDoEventoEsportivo(int horaDoEventoEsportivo) {
		this.horaDoEventoEsportivo = horaDoEventoEsportivo;
	}

	public String getLocalDoEvento() {
		return localDoEvento;
	}

	public void setLocalDoEvento(String localDoEvento) {
		this.localDoEvento = localDoEvento;
	}

}
